# -*- coding: utf-8 -*-import jdatetimefrom django import templateregister = template.Library()def farsi_text(number):    #TODO: TO BE IMPLEMENTED LATER    return number@register.filter()def get_jdate(date):    return jdatetime.GregorianToJalali(date.year, date.month, date.day)def farsi_text_month(number):    text = ['', u'فروردین', u'اردیبهشت', u'خرداد', u'تیر', u'مرداد', u'شهریور',            u'مهر', u'آبان', u'آذر', u'دی', u'بهمن', u'اسفند', ]    return text[number]@register.filter()def farsi_digits(string):    transition = {'0': u'۰', '1': u'۱', '2': u'۲', '3': u'۳', '4': u'۴',                  '5': u'۵', '6': u'۶', '7': u'۷', '8': u'۸', '9': u'۹', u'،': u' و '}    for en in transition:        string = string.replace(en, transition[en])    return string@register.filter()def jdateformat(jdate, _format):    """formats date or datetime to a specific format"""    return _format.format(d=jdate.jday, m=jdate.jmonth, y=jdate.jyear,                          D=farsi_text(jdate.jday), M=farsi_text_month(jdate.jmonth), Y=farsi_text(jdate.jyear))